mapper.insert:
INSERT INTO member (
	m_no, m_id, m_pwd, m_name, m_email, m_tel, m_type, m_active, m_intro
) VALUES (
	m_no_seq.NEXTVAL, #{m_id}, #{m_pwd}, #{m_name}, #{m_email}, #{m_tel}, #{m_type}, #{m_active}, #{m_intro}
)

INSERT INTO gosu(
	g_no, c_no
) VALUES (
	#{g_no.m_no}, #{c_no.c_no}
)

mapper.select:
SELECT count(*) FROM member 
WHERE m_id = #{m_id}
AND m_pwd = #{m_pwd}
